import axios from 'axios';

const handleFormSubmit = (e) => {
    e.preventDefault();
    if (username === '' || password === '') {
      setError(true);
    } else {
      axios.post('/server/users', {
        name: username,
        password: password
      })
      .then((response) => {
        setSubmitted(true);
        setError(false);
      })
      .catch((error) => {
        console.log(error);
        setError(true);
      });
    }
  }

  const handleSubmit = (e) => {
    handleFormSubmit(e);
  };
