import { useState } from 'react';
import axios from 'axios';

const handleFormSubmit = (e) => {
  e.preventDefault();
  if (username === '' || password === '') {
    setError(true);
  } else {
    axios.post('/api/users', {
      name: name,
      password: password
    })
    .then((response) => {
      setSubmitted(true);
      setError(false);
    })
    .catch((error) => {
      console.log(error);
      setError(true);
    });
  }
};
